  Widget _buildSettingTile(
    String title,
    String subtitle,
    String icon,
    bool value, {
    bool enabled = true,
  }) {
    return ListTile(
      leading: Container(
        padding: EdgeInsets.all(2.w),
        decoration: BoxDecoration(
          color: AppTheme.lightTheme.colorScheme.primary.withValues(alpha: 0.1),
