widgets.notification_card_widget.dart:
import 'package:flutter/material.dart';
import 'package:sizer/sizer.dart';

import '../../../core/app_export.dart';

class NotificationCardWidget extends StatelessWidget {
  final Map<String, dynamic> notification;
  final VoidCallback? onTap;
  final VoidCallback? onMarkAsRead;
  final VoidCallback? onDelete;

  const NotificationCardWidget({
    Key? key,
    required this.notification,
    this.onTap,
    this.onMarkAsRead,
    this.onDelete,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    final bool isRead = notification['isRead'] as bool? ?? false;
    final String type = notification['type'] as String? ?? 'post';
    final bool isEmergency = type == 'emergency';

    return Dismissible(
      key: Key(notification['id'].toString()),
      background: Container(
        alignment: Alignment.centerLeft,
        padding: EdgeInsets.only(left: 4.w),
        decoration: BoxDecoration(
          color: AppTheme.lightTheme.colorScheme.primary.withValues(alpha: 0.1),
          borderRadius: BorderRadius.circular(12),
        ),
        child: CustomIconWidget(
          iconName: 'mark_email_read',
          color: AppTheme.lightTheme.colorScheme.primary,
          size: 6.w,
        ),
      ),
      secondaryBackground: Container(
        alignment: Alignment.centerRight,
        padding: EdgeInsets.only(right: 4.w),
        decoration: BoxDecoration(
          color: AppTheme.lightTheme.colorScheme.error.withValues(alpha: 0.1),
          borderRadius: BorderRadius.circular(12),
        ),
        child: CustomIconWidget(
          iconName: 'delete',
          color: AppTheme.lightTheme.colorScheme.error,
          size: 6.w,
        ),
      ),
      confirmDismiss: (direction) async {
        if (direction == DismissDirection.startToEnd) {
          onMarkAsRead?.call();
          return false;
        } else {
          return await _showDeleteConfirmation(context);
        }
      },
      child: GestureDetector(
        onTap: onTap,
        onLongPress: () => _showContextMenu(context),
        child: Container(
          margin: EdgeInsets.symmetric(horizontal: 4.w, vertical: 1.h),
          padding: EdgeInsets.all(3.w),
          decoration: BoxDecoration(
            color: isEmergency
                ? AppTheme.lightTheme.colorScheme.error.withValues(alpha: 0.05)
                : AppTheme.lightTheme.colorScheme.surface,
            borderRadius: BorderRadius.circular(12),
            border: Border.all(
              color: isEmergency
                  ? AppTheme.lightTheme.colorScheme.error.withValues(alpha: 0.3)
                  : isRead
                      ? Colors.transparent
                      : AppTheme.lightTheme.colorScheme.primary
                          .withValues(alpha: 0.3),
              width: isEmergency ? 2 : (isRead ? 0 : 1),
            ),
            boxShadow: [
              BoxShadow(
                color: Colors.black.withValues(alpha: 0.05),
                blurRadius: 4,
                offset: const Offset(0, 2),
              ),
            ],
          ),
          child: Row(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              _buildAvatar(),
              SizedBox(width: 3.w),
              Expanded(
                child: Column(
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    _buildHeader(),
                    SizedBox(height: 1.h),
                    _buildContent(),
                    SizedBox(height: 1.h),
                    _buildFooter(),
                  ],
                ),
              ),
              if (!isRead) _buildUnreadIndicator(),
            ],
          ),
        ),
      ),
    );
  }

  Widget _buildAvatar() {
    final String? avatarUrl = notification['senderAvatar'] as String?;
    final String type = notification['type'] as String? ?? 'post';

    return Container(
      width: 12.w,
      height: 12.w,
      decoration: BoxDecoration(
        shape: BoxShape.circle,
        border: Border.all(
          color: type == 'emergency'
              ? AppTheme.lightTheme.colorScheme.error
              : AppTheme.lightTheme.colorScheme.primary.withValues(alpha: 0.3),
          width: 2,
        ),
      ),
      child: ClipOval(
        child: avatarUrl != null
            ? CustomImageWidget(
                imageUrl: avatarUrl,
                width: 12.w,
                height: 12.w,
                fit: BoxFit.cover,
              )
            : Container(
                color: AppTheme.lightTheme.colorScheme.primary
                    .withValues(alpha: 0.1),
                child: CustomIconWidget(
                  iconName: _getTypeIcon(type),
                  color: type == 'emergency'
                      ? AppTheme.lightTheme.colorScheme.error
                      : AppTheme.lightTheme.colorScheme.primary,
                  size: 6.w,
                ),
              ),
      ),
    );
  }

  Widget _buildHeader() {
    final String senderName =
        notification['senderName'] as String? ?? 'Universidad';
    final String type = notification['type'] as String? ?? 'post';
    final bool isEmergency = type == 'emergency';

    return Row(
      children: [
        Expanded(
          child: Text(
            senderName,
            style: AppTheme.lightTheme.textTheme.titleSmall?.copyWith(
              fontWeight: FontWeight.w600,
              color: isEmergency
                  ? AppTheme.lightTheme.colorScheme.error
                  : AppTheme.lightTheme.colorScheme.onSurface,
            ),
            maxLines: 1,
            overflow: TextOverflow.ellipsis,
          ),
        ),
        SizedBox(width: 2.w),
        _buildTypeChip(type),
      ],
    );
  }

  Widget _buildTypeChip(String type) {
    final Map<String, dynamic> typeConfig = _getTypeConfig(type);

    return Container(
      padding: EdgeInsets.symmetric(horizontal: 2.w, vertical: 0.5.h),
      decoration: BoxDecoration(
        color: (typeConfig['color'] as Color).withValues(alpha: 0.1),
        borderRadius: BorderRadius.circular(8),
      ),
      child: Text(
        typeConfig['label'] as String,
        style: AppTheme.lightTheme.textTheme.labelSmall?.copyWith(
          color: typeConfig['color'] as Color,
          fontWeight: FontWeight.w500,
        ),
      ),
    );
  }

  Widget _buildContent() {
    final String title = notification['title'] as String? ?? '';
    final String message = notification['message'] as String? ?? '';
    final bool isRead = notification['isRead'] as bool? ?? false;

    return Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: [
        if (title.isNotEmpty)
          Text(
            title,
            style: AppTheme.lightTheme.textTheme.bodyMedium?.copyWith(
              fontWeight: isRead ? FontWeight.w400 : FontWeight.w600,
              color: AppTheme.lightTheme.colorScheme.onSurface,
            ),
            maxLines: 2,
            overflow: TextOverflow.ellipsis,
          ),
        if (title.isNotEmpty && message.isNotEmpty) SizedBox(height: 0.5.h),
        if (message.isNotEmpty)
          Text(
            message,
            style: AppTheme.lightTheme.textTheme.bodySmall?.copyWith(
              color: AppTheme.lightTheme.colorScheme.onSurfaceVariant,
            ),
            maxLines: 3,
            overflow: TextOverflow.ellipsis,
          ),
      ],
    );
  }

  Widget _buildFooter() {
    final DateTime timestamp =
        notification['timestamp'] as DateTime? ?? DateTime.now();
    final String timeAgo = _getTimeAgo(timestamp);

    return Row(
      children: [
        CustomIconWidget(
          iconName: 'access_time',
          color: AppTheme.lightTheme.colorScheme.onSurfaceVariant,
          size: 4.w,
        ),
        SizedBox(width: 1.w),
        Text(
          timeAgo,
          style: AppTheme.lightTheme.textTheme.labelSmall?.copyWith(
            color: AppTheme.lightTheme.colorScheme.onSurfaceVariant,
          ),
        ),
      ],
    );
  }

  Widget _buildUnreadIndicator() {
    return Container(
      width: 2.w,
      height: 2.w,
      decoration: BoxDecoration(
        color: AppTheme.lightTheme.colorScheme.primary,
        shape: BoxShape.circle,
      ),
    );
  }

  String _getTypeIcon(String type) {
    switch (type) {
      case 'post':
        return 'article';
      case 'event':
        return 'event';
      case 'emergency':
        return 'warning';
      case 'system':
        return 'settings';
      case 'rsvp':
        return 'event_available';
      default:
        return 'notifications';
    }
  }

  Map<String, dynamic> _getTypeConfig(String type) {
    switch (type) {
      case 'post':
        return {
          'label': 'PublicaciÃ³n',
          'color': AppTheme.lightTheme.colorScheme.primary,
        };
      case 'event':
        return {
          'label': 'Evento',
          'color': AppTheme.lightTheme.colorScheme.tertiary,
        };
      case 'emergency':
        return {
          'label': 'Emergencia',
          'color': AppTheme.lightTheme.colorScheme.error,
        };
      case 'system':
        return {
          'label': 'Sistema',
          'color': AppTheme.lightTheme.colorScheme.secondary,
        };
      case 'rsvp':
        return {
          'label': 'RSVP',
          'color': Colors.green,
        };
      default:
        return {
          'label': 'General',
          'color': AppTheme.lightTheme.colorScheme.secondary,
        };
    }
  }

  String _getTimeAgo(DateTime timestamp) {
    final now = DateTime.now();
    final difference = now.difference(timestamp);

    if (difference.inMinutes < 1) {
      return 'Ahora';
    } else if (difference.inMinutes < 60) {
      return 'hace ${difference.inMinutes}m';
    } else if (difference.inHours < 24) {
      return 'hace ${difference.inHours}h';
    } else if (difference.inDays < 7) {
      return 'hace ${difference.inDays}d';
    } else {
      return '${timestamp.day}/${timestamp.month}/${timestamp.year}';
    }
  }

  Future<bool> _showDeleteConfirmation(BuildContext context) async {
    return await showDialog<bool>(
          context: context,
          builder: (context) => AlertDialog(
            title: Text(
              'Eliminar notificaciÃ³n',
              style: AppTheme.lightTheme.textTheme.titleMedium,
            ),
            content: Text(
              'Â¿EstÃ¡s seguro de que quieres eliminar esta notificaciÃ³n?',
              style: AppTheme.lightTheme.textTheme.bodyMedium,
            ),
            actions: [
              TextButton(
                onPressed: () => Navigator.of(context).pop(false),
                child: Text('Cancelar'),
              ),
              TextButton(
                onPressed: () {
                  Navigator.of(context).pop(true);
                  onDelete?.call();
                },
                child: Text(
                  'Eliminar',
                  style:
                      TextStyle(color: AppTheme.lightTheme.colorScheme.error),
                ),
              ),
            ],
          ),
        ) ??
        false;
  }

  void _showContextMenu(BuildContext context) {
    showModalBottomSheet(
      context: context,
      shape: RoundedRectangleBorder(
        borderRadius: BorderRadius.vertical(top: Radius.circular(16)),
      ),
      builder: (context) => Container(
        padding: EdgeInsets.all(4.w),
        child: Column(
          mainAxisSize: MainAxisSize.min,
          children: [
            Container(
              width: 10.w,
              height: 0.5.h,
              decoration: BoxDecoration(
                color: AppTheme.lightTheme.colorScheme.onSurfaceVariant
                    .withValues(alpha: 0.3),
                borderRadius: BorderRadius.circular(2),
              ),
            ),
            SizedBox(height: 2.h),
            ListTile(
              leading: CustomIconWidget(
                iconName: 'mark_email_read',
                color: AppTheme.lightTheme.colorScheme.primary,
                size: 6.w,
              ),
              title: Text('Marcar como leÃ­da'),
              onTap: () {
                Navigator.pop(context);
                onMarkAsRead?.call();
              },
            ),
            ListTile(
              leading: CustomIconWidget(
                iconName: 'delete',
                color: AppTheme.lightTheme.colorScheme.error,
                size: 6.w,
              ),
              title: Text('Eliminar'),
              onTap: () {
                Navigator.pop(context);
                _showDeleteConfirmation(context).then((confirmed) {
                  if (confirmed) onDelete?.call();
                });
              },
            ),
            ListTile(
              leading: CustomIconWidget(
                iconName: 'settings',
                color: AppTheme.lightTheme.colorScheme.secondary,
                size: 6.w,
              ),
              title: Text('ConfiguraciÃ³n de notificaciones'),
              onTap: () {
                Navigator.pop(context);
                // Navigate to notification settings
              },
            ),
          ],
        ),
      ),
    );
  }
}

widgets.notification_filter_chips_widget.dart:
import 'package:flutter/material.dart';
import 'package:sizer/sizer.dart';

import '../../../core/app_export.dart';

class NotificationFilterChipsWidget extends StatelessWidget {
  final String selectedFilter;
  final Function(String) onFilterChanged;
  final Map<String, int> filterCounts;

  const NotificationFilterChipsWidget({
    Key? key,
    required this.selectedFilter,
    required this.onFilterChanged,
    required this.filterCounts,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    final List<Map<String, dynamic>> filters = [
      {
        'key': 'all',
        'label': 'Todas',
        'icon': 'notifications',
      },
      {
        'key': 'post',
        'label': 'Publicaciones',
        'icon': 'article',
      },
      {
        'key': 'event',
        'label': 'Eventos',
        'icon': 'event',
      },
      {
        'key': 'emergency',
        'label': 'Emergencia',
        'icon': 'warning',
      },
      {
        'key': 'system',
        'label': 'Sistema',
        'icon': 'settings',
      },
    ];

    return Container(
      height: 6.h,
      child: ListView.separated(
        scrollDirection: Axis.horizontal,
        padding: EdgeInsets.symmetric(horizontal: 4.w),
        itemCount: filters.length,
        separatorBuilder: (context, index) => SizedBox(width: 2.w),
        itemBuilder: (context, index) {
          final filter = filters[index];
          final String key = filter['key'] as String;
          final String label = filter['label'] as String;
          final String icon = filter['icon'] as String;
          final bool isSelected = selectedFilter == key;
          final int count = filterCounts[key] ?? 0;

          return GestureDetector(
            onTap: () => onFilterChanged(key),
            child: AnimatedContainer(
              duration: const Duration(milliseconds: 200),
              padding: EdgeInsets.symmetric(horizontal: 4.w, vertical: 1.h),
              decoration: BoxDecoration(
                color: isSelected
                    ? AppTheme.lightTheme.colorScheme.primary
                    : AppTheme.lightTheme.colorScheme.surface,
                borderRadius: BorderRadius.circular(20),
                border: Border.all(
                  color: isSelected
                      ? AppTheme.lightTheme.colorScheme.primary
                      : AppTheme.lightTheme.colorScheme.outline
                          .withValues(alpha: 0.3),
                  width: 1,
                ),
                boxShadow: isSelected
                    ? [
                        BoxShadow(
                          color: AppTheme.lightTheme.colorScheme.primary
                              .withValues(alpha: 0.3),
                          blurRadius: 8,
                          offset: const Offset(0, 2),
                        ),
                      ]
                    : [
                        BoxShadow(
                          color: Colors.black.withValues(alpha: 0.05),
                          blurRadius: 4,
                          offset: const Offset(0, 1),
                        ),
                      ],
              ),
              child: Row(
                mainAxisSize: MainAxisSize.min,
                children: [
                  CustomIconWidget(
                    iconName: icon,
                    color: isSelected
                        ? AppTheme.lightTheme.colorScheme.onPrimary
                        : key == 'emergency'
                            ? AppTheme.lightTheme.colorScheme.error
                            : AppTheme.lightTheme.colorScheme.onSurface,
                    size: 4.w,
                  ),
                  SizedBox(width: 1.w),
                  Text(
                    label,
                    style: AppTheme.lightTheme.textTheme.labelMedium?.copyWith(
                      color: isSelected
                          ? AppTheme.lightTheme.colorScheme.onPrimary
                          : AppTheme.lightTheme.colorScheme.onSurface,
                      fontWeight:
                          isSelected ? FontWeight.w600 : FontWeight.w500,
                    ),
                  ),
                  if (count > 0) ...[
                    SizedBox(width: 1.w),
                    Container(
                      padding: EdgeInsets.symmetric(
                          horizontal: 1.5.w, vertical: 0.2.h),
                      decoration: BoxDecoration(
                        color: isSelected
                            ? AppTheme.lightTheme.colorScheme.onPrimary
                            : key == 'emergency'
                                ? AppTheme.lightTheme.colorScheme.error
                                : AppTheme.lightTheme.colorScheme.primary,
                        borderRadius: BorderRadius.circular(10),
                      ),
                      child: Text(
                        count > 99 ? '99+' : count.toString(),
                        style:
                            AppTheme.lightTheme.textTheme.labelSmall?.copyWith(
                          color: isSelected
                              ? AppTheme.lightTheme.colorScheme.primary
                              : AppTheme.lightTheme.colorScheme.onPrimary,
                          fontWeight: FontWeight.w600,
                          fontSize: 9.sp,
                        ),
                      ),
                    ),
                  ],
                ],
              ),
            ),
          );
        },
      ),
    );
  }
}

splash_screen.splash_screen.dart:
import 'package:flutter/material.dart';
import 'package:flutter/services.dart';
import 'package:sizer/sizer.dart';

import '../../../core/app_export.dart';
import '../../services/supabase_service.dart';

class SplashScreen extends StatefulWidget {
  const SplashScreen({super.key});

  @override
  State<SplashScreen> createState() => _SplashScreenState();
}

class _SplashScreenState extends State<SplashScreen>
    with SingleTickerProviderStateMixin {
  late AnimationController _animationController;
  late Animation<double> _scaleAnimation;
  late Animation<double> _fadeAnimation;
  bool _isInitializing = true;
  String _statusMessage = 'Inicializando UniConnect...';

  @override
  void initState() {
    super.initState();
    _setupAnimations();
    _initializeApp();
  }

  void _setupAnimations() {
    _animationController = AnimationController(
      duration: const Duration(milliseconds: 2000),
      vsync: this,
    );

    _scaleAnimation = Tween<double>(
      begin: 0.8,
      end: 1.0,
    ).animate(CurvedAnimation(
      parent: _animationController,
      curve: Curves.elasticOut,
    ));

    _fadeAnimation = Tween<double>(
      begin: 0.0,
      end: 1.0,
    ).animate(CurvedAnimation(
      parent: _animationController,
      curve: const Interval(0.0, 0.6, curve: Curves.easeIn),
    ));

    _animationController.forward();
  }

  Future<void> _initializeApp() async {
    try {
      setState(() {
        _statusMessage = 'Inicializando servicios...';
      });
      // Inicializar Supabase
      await SupabaseService.initialize();

      setState(() {
        _statusMessage = 'Verificando autenticaciÃ³n...';
      });
      await Future.delayed(const Duration(milliseconds: 500));

      setState(() {
        _statusMessage = 'Cargando preferencias...';
      });
      await Future.delayed(const Duration(milliseconds: 400));

      setState(() {
        _statusMessage = 'Configurando universidad...';
      });
      await Future.delayed(const Duration(milliseconds: 400));

      setState(() {
        _statusMessage = 'Preparando contenido...';
      });
      await Future.delayed(const Duration(milliseconds: 300));

      setState(() {
        _isInitializing = false;
        _statusMessage = 'Â¡Listo!';
      });

      // Wait for animation to complete
      await Future.delayed(const Duration(milliseconds: 300));

      // Navegar segÃºn autenticaciÃ³n
      await _navigateToNextScreen();
    } catch (e) {
      setState(() {
        _isInitializing = false;
        _statusMessage = 'Error de conexiÃ³n';
      });
      await Future.delayed(const Duration(seconds: 2));
      _showRetryOption();
    }
  }

  Future<void> _navigateToNextScreen() async {
    // Simulate authentication check
    final bool isAuthenticated = await _checkAuthenticationStatus();
    final bool isFirstTime = await _checkFirstTimeUser();

    if (!mounted) return;

    if (isAuthenticated) {
      Navigator.pushReplacementNamed(context, '/main-feed-screen');
    } else if (isFirstTime) {
      // For now, navigate to login as onboarding is not implemented
      Navigator.pushReplacementNamed(context, '/login-screen');
    } else {
      Navigator.pushReplacementNamed(context, '/login-screen');
    }
  }

  Future<bool> _checkAuthenticationStatus() async {
    // Simulate checking stored authentication token
    await Future.delayed(const Duration(milliseconds: 200));
    // Return false for demo - in real app, check stored credentials
    return false;
  }

  Future<bool> _checkFirstTimeUser() async {
    // Simulate checking if user has opened app before
    await Future.delayed(const Duration(milliseconds: 100));
    // Return true for demo - in real app, check stored preferences
    return true;
  }

  void _showRetryOption() {
    if (!mounted) return;

    showDialog(
      context: context,
      barrierDismissible: false,
      builder: (BuildContext context) {
        return AlertDialog(
          backgroundColor: AppTheme.lightTheme.colorScheme.surface,
          shape: RoundedRectangleBorder(
            borderRadius: BorderRadius.circular(16.0),
          ),
          title: Text(
            'Error de ConexiÃ³n',
            style: AppTheme.lightTheme.textTheme.titleLarge?.copyWith(
              color: AppTheme.lightTheme.colorScheme.onSurface,
            ),
          ),
          content: Text(
            'No se pudo conectar con el servidor. Verifica tu conexiÃ³n a internet e intÃ©ntalo de nuevo.',
            style: AppTheme.lightTheme.textTheme.bodyMedium?.copyWith(
              color: AppTheme.lightTheme.colorScheme.onSurfaceVariant,
            ),
          ),
          actions: [
            TextButton(
              onPressed: () {
                Navigator.of(context).pop();
                Navigator.pushReplacementNamed(context, '/login-screen');
              },
              child: Text(
                'Continuar sin conexiÃ³n',
                style: TextStyle(
                  color: AppTheme.lightTheme.colorScheme.secondary,
                ),
              ),
            ),
            ElevatedButton(
              onPressed: () {
                Navigator.of(context).pop();
                setState(() {
                  _isInitializing = true;
                  _statusMessage = 'Reintentando...';
                });
                _initializeApp();
              },
              style: ElevatedButton.styleFrom(
                backgroundColor: AppTheme.lightTheme.colorScheme.primary,
                foregroundColor: AppTheme.lightTheme.colorScheme.onPrimary,
              ),
              child: const Text('Reintentar'),
            ),
          ],
        );
      },
    );
  }

  @override
  void dispose() {
